import React, { Component } from "react";
import "./index.css"
import PubSub from "pubsub-js"
export default class List extends Component {
  
  state={
    users:[],
    isFirst:true,
    isLoading:false,
    err:""
  }
  componentDidMount(){
    //订阅消息
    //下划线占个位
    this.taken=PubSub.subscribe("Header",(_,data)=>{
      this.setState(data)//整个改数据
    })
  }
  componentWillUY
  render() {
    const {users,isFirst,isLoading,err}=this.state
    return <div className="cs-List-x">
              { 
                isFirst?<h2>Welcome to research</h2>:
                isLoading?<h2>Loading...</h2>:
                err?<h2 style={{color:"red"}}>{err.message}</h2>:
                users.map((item)=>{
                    return (
                      <div className="cs-List-img-x" key={item.id}>
                        <a href={item.html_url} className="cs-List-navi" >
                            <img src={item.avatar_url} className="cs-List-img"/>
                        </a>
                        <p className="cs-List-img-p"> {item.login}</p>
                      </div>
                    )
                })}
            </div>;
            //{this.sta}
  }
}
