import React, { Component } from 'react'
import PubSub from "pubsub-js"
export default class From2 extends Component {
    state={
        todos:[{id:1,name:"吃饭"}]
    }
    componentDidMount(){
        this.taken=PubSub.subscribe("From2",(msg,data)=>{
            this.setState({todos:[{id:this.state.todos[-1].id+1,name:data},...this.state.todos]})
        })
    }
    componentWillUnmount(){
        PubSub.unsubscribe(this.taken)
    }
    render() {
        return (
        <div className="cs-Form-x">
            <h1 className="cs-From-title">Form2</h1>
            <div className="cs-From-value">
                {this.state.todos.map((todo)=>{
                    return(
                        <div className="cs-Form-todo" key={todo.id}>
                            <input type="check" className="cs-todo-input"></input>
                            <p className="cs-todo-p">{todo.name}</p>
                            <button className="cs-todo-button">删除</button>
                        </div>
                    )
                })}
            </div>
        </div>
        )
    }
}
