import React from 'react'
import "./index.css"
export default function Item(props) {
    //解构赋值
    const {id,value,done}=props
    const checkTodo =()=>{
      props.checkTodo(id)
    }
  return (
    <div className="cs-todo-x">
      <button className="cs-todo-check" type="checkbox" defaultChecked={done} onClick={checkTodo}></button>
      <p className="cs-todo-value">{value}</p>
      <button className="cs-todo-delete">Delete</button>
    </div>
  )
}
