import React ,{useState,useEffect}from 'react'
import "./index.css"
export default function Footer(props) {
  const [checkNum,setCheckNum]=useState(0)
  //初次挂载和每次刷新均执行！
  useEffect(()=>{
    const result = props.todos.reduce((num,item)=>{
        if (item.done){
            return num+1
        }else{
            return num
        }
    },0)
    setCheckNum(()=>result)
  })
  const checkAllTodos=()=>{
    console("checkAll")
  }
  const deleteAll=()=>{
    console("DeleteAll")
  }
  return (
    <div className="cs-footer-x">
      <input type="checkbox" checked={checkNum==props.todos.length-checkNum?true:false} onChange={checkAllTodos} className="cs-footer-check"/>
      <p className="cs-footer-statistic">Finished:{checkNum}unFinished:{props.todos.length-checkNum}</p>
      <button className="cs-footer-deleteAll" onClick={deleteAll}>DeleteAllFinished</button>
    </div>
  )
}
