//外壳组件，包含其他所有子组件
/*ES6的引入方式=>分别暴露和统一暴露，这一块有多种暴露*/
/*React文件里面暴露了Component export default Component*/
/*也暴露了本身文件 export default React*/
//不是解构赋值！！！！
import React,{Component} from 'react'
//要有react，react-dom不需要，因为这一块不做渲染
/*文件名好像对大小写不敏感，建议加个js*/
import Hello from "./components/Hello/Hello";

//创建同时暴露app组件
export default class App extends Component{
    render(){
        return (
            <div>
                <Hello/>
            </div>
            
        )
    }
}

