import React, { Component } from 'react'
import PubSub from "pubsub-js"
export default class Header extends Component {
    toForm1Data=()=>{
        const {value}=this.input
        console.log(value)
        PubSub.publish("Form1",value)
    }

    toForm2Data=()=>{
        const {value}=this.input.target
        console.log(value)
        PubSub.publish("Form2",value)
    }
    render() {
    return (
      <div className="cs-Header-x">
        <h1 className="cs-Header-little">
            WelCome to check different routes
        </h1>
        <input type="text" className="cs-Header-input" placeholder="Hello input something?" ref={(c)=>this.input=c}></input>
        <button className="cs-Header-button" onClick={this.toForm1Data}>Form1</button>
        <button className="cs-Header-button" onClick={this.toForm2Data}>Form2</button>
      </div>
    )
  }
}
