import React,{useRef} from 'react'

export default function App() {
  const Input=useRef(null)
  const focusOn=()=>{
    console.log(Input.current)
    Input.current.focus()//
  }
  return (
    <div>
      <input type="text" ref={Input}></input>
      <button onClick={focusOn}>click focus</button>
    </div>
  )
}<input type="text"></input>
