import React, { Component } from 'react'
import axios from "axios"
import "./index.css"
export default class Header extends Component {
  search=()=>{
    //获取用户输入
    //其实可以连续解构赋值**小技巧
    //const {keyWord:{value}}=this 拿this里面的keyWord里面的value 连续解构
    //带上重命名 {keyWord:{value:keyWordValue}}=this 可以将value赋值给keyWordValue

    //开始搜索的时候显示loading
    this.props.updateState({isFirst:false,isLoading:true})
    const keyWord=this.keyWord.value
    console.log(keyWord)
    //发送网络请求
    //https://api.github.com/search/users?q=xxx q指定用户名(github访问多次会反馈错误，我们可以设置代理，详细看视频github搜索第一节吧)
    axios.get(`https://api.github.com/search/users?q=${keyWord}`).then(
      response=>{
        this.props.updateState({users:response.data.items,err:""},isLoa)
      },
      err=>{
        this.props.updateState({err:err})
      }
    )
  }
  render() {
    return (
      <div className="cs-Header-x">
        <h1 className="cs-Header-title">Search Github Users</h1>
        <div className="cs-Header-search">
            <input type="text" placeholder="enter the name you search" className="cs-Header-input" ref={c=>this.keyWord=c}/>
            <button className='cs-Header-button' onClick={this.search}>Search</button>
        </div>
      </div>
    )
  }
}
